    public static CPPerformanceBean getPragathiData(JSONObject object,String LMObject, String paramParentObject, String paramObject, String name){
        CPPerformanceBean bean =null;
        try {
            JSONObject jsonObject = object;
            jsonObject = jsonObject.getJSONObject(LMObject);
            jsonObject = jsonObject.getJSONObject(paramParentObject);
            jsonObject = jsonObject.getJSONObject(paramObject);
            bean = new CPPerformanceBean();
            if (paramObject!=null&&(paramObject.equalsIgnoreCase("greenoutlet")
                    ||paramObject.equalsIgnoreCase("eco")
                    ||paramObject.equalsIgnoreCase("dayswith20bill")
                    ||paramObject.equalsIgnoreCase("mon1")
                    ||paramObject.equalsIgnoreCase("mon2")
                    ||paramObject.equalsIgnoreCase("mon3")
                    ||paramObject.equalsIgnoreCase("mon4")
                    ||paramObject.equalsIgnoreCase("mon5")
                    ||paramObject.equalsIgnoreCase("mon6"))) {
                bean.setTarget(jsonObject.getDouble(JSONResponse.target.toString()));
                bean.setAchvmnt(jsonObject.getDouble(JSONResponse.achvmnt.toString()));
                bean.setEarning(jsonObject.getDouble(JSONResponse.earning.toString()));
            }else if(paramObject!=null&&(paramObject.equalsIgnoreCase("bcra")||
                    paramObject.equalsIgnoreCase("diary"))||
                    paramObject.equalsIgnoreCase("innovation")){
                double target=0.0;
                double achvmnt=0.0;
                double earning=0.0;
                try {
                    target = jsonObject.getDouble(JSONResponse.target.toString());
                    if (target!=0){
                        target = target/1000;
                    }
                } catch (Throwable e) {
                    target=0.0;
                }
                try {
                    achvmnt = jsonObject.getDouble(JSONResponse.achvmnt.toString());
                    if (achvmnt!=0){
                        achvmnt = achvmnt/1000;
                    }
                } catch (Throwable e) {
                    achvmnt=0.0;
                }
                try {
                    earning = jsonObject.getDouble(JSONResponse.earning.toString());
                    if (earning!=0){
                        earning = earning/1000;
                    }
                } catch (Throwable e) {
                    earning=0.0;
                }
                bean.setTarget(target);
                bean.setAchvmnt(achvmnt);
                bean.setEarning(earning);
            }

            bean.setParamName(name);
        } catch (Throwable e) {
            e.printStackTrace();
        }
        return bean==null?new CPPerformanceBean():bean;
    }
