    public static boolean getGRFromDatavalut(Context context){
        boolean ischeck=false;
        Set<String> set = new HashSet<>();
//        String configDateFormat = ConstantsUtils.getConfigTypeDateFormat(context);
        SharedPreferences sharedPreferences = context.getSharedPreferences(Constants.PREFS_NAME, 0);
        set = sharedPreferences.getStringSet(Constants.MaterialDocs, null);
        if (set != null && !set.isEmpty()){
            Iterator itr = set.iterator();
            while (itr.hasNext()){
                String store = null, deviceNo = "";
                deviceNo = itr.next().toString();
                store =  ConstantsUtils.getFromDataVault(deviceNo,context);
                try {
                    JSONObject fetchJsonHeaderObject = new JSONObject(store);
                    if (fetchJsonHeaderObject.getString(Constants.EntityType).equalsIgnoreCase(Constants.MaterialDocs)) {
                        ischeck = true;
                        break;
                    }
                } catch (JSONException e) {
                    e.printStackTrace();
                }
            }
        }
        return ischeck;
    }
